mapper.select:
SELECT

FROM zxyw_user
WHERE username = #{username}
AND password =#{password}

select
count(1)
from zxyw_user
where username= #{username}
and question = #{question}
and answer = #{answer}

mapper.update:
UPDATE zxyw_user
SET password = #{password},update_time = now()
WHERE username = #{username}

mapper.select:
SELECT
count(1)
from zxyw_user
where password = #{password}
and id = #{userId}

select count(1) from zxyw_user
where email = #{email}
and id != #{userId}
